import { useSelector, useDispatch } from 'react-redux'
import { deleteBook, toggleFavorite } from '../../redux/books/actionCreators'
import {
  selectTitleFilter,
  selectAuthorFilter,
  selectOnlyFavoriteFilter,
  setOnlyFavoriteFilter,
} from '../../redux/slices/filterSlice'
import { BsBookmarkStarFill, BsBookmarkStar } from 'react-icons/bs'

import './BookList.css'

const BookList = () => {
  const books = useSelector((state) => state.books) // отображаем нашу часть нашего состояния через подписку хука useSelector (если изменяется он ререндериться весь этот компонент)
  const titleFilter = useSelector(selectTitleFilter) // подписались на изменение поля фильтр по титлу
  const authorFilter = useSelector(selectAuthorFilter) // подписались на изменение поля фильтр по автору
  const onlyFavoriteFilter = useSelector(selectOnlyFavoriteFilter)

  const dispatch = useDispatch()

  const handleDeleteBook = (id) => {
    dispatch(deleteBook(id))
  }

  const handleToggleFavorite = (id) => {
    dispatch(toggleFavorite(id))
  }

  const filteredBooks = books.filter((book) => {
    // поиск осуществляем по титлу книги и вводимому значению пользователя все приводим к нижнему регистру создаться новый обьект
    const matchesTitle = book.title
      .toLowerCase()
      .includes(titleFilter.toLowerCase())

    const matchesAuthor = book.author
      .toLowerCase()
      .includes(authorFilter.toLowerCase())

    return matchesTitle && matchesAuthor
  })

  const handleOnlyFavorite = books.filter((book) => {
    const FavoriteList = []
    if (onlyFavoriteFilter === book.isFavorite) {
      FavoriteList.append(book)
      return FavoriteList
    } else {
      return books
    }
  })

  dispatch(setOnlyFavoriteFilter(handleOnlyFavorite))

  return (
    <div className="app-block book-list">
      <h2>BookList</h2>
      {books.length === 0 ? (
        <p>No books availiable</p>
      ) : (
        <ul>
          {filteredBooks.map((book, i) => (
            <li key={book.id}>
              <div className="book-info">
                {++i}. {book.title} by <strong>{book.author}</strong>
              </div>
              <div className="book-actions">
                <span onClick={() => handleToggleFavorite(book.id)}>
                  {book.isFavorite ? (
                    <BsBookmarkStarFill
                      onClick={handleOnlyFavorite}
                      className="star-icon"
                    />
                  ) : (
                    <BsBookmarkStar
                      onClick={handleOnlyFavorite}
                      className="star-icon"
                    />
                  )}
                </span>
                <button onClick={() => handleDeleteBook(book.id)}>
                  Delete Book
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  )
}

export default BookList
