import { configureStore } from '@reduxjs/toolkit'
import booksReducer from './books/reducer'
import filteReducer from './slices/filterSlice'
// общее состояние нашего  приложения а books это часть нашего общего состояния
const store = configureStore({
  reducer: { books: booksReducer, filter: filteReducer },
})

export default store
