import { useSelector } from 'react-redux'
import './BookList.css'

const BookList = () => {
  const books = useSelector((state) => state.books) // отображаем нашу часть нашего состояния через подписку хука useSelector (если изменяется он ререндериться весь этот компонент)
  return (
    <div className="app-block book-list">
      <h2>BookLIst</h2>
      {books.length === 0 ? (
        <p>No books availiable</p>
      ) : (
        <ul>
          {books.map((book, i) => (
            <li key={book.id}>
              <div className="book-info">
                {++i}. {book.title} by <strong>{book.author}</strong>
              </div>
              <div>
                <button>Delete Book</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  )
}

export default BookList
